mapper.select:
SELECT *
FROM OCR

  AND ocr_no=#{ocr_no}
  AND usr_id=#{usr_id}
  AND ocr_fileName=#{ocr_fileName}
  AND ocr_filePath=#{ocr_filePath}
  AND ocr_datetime=#{ocr_datetime}
  AND ocr_ocrResult=#{ocr_ocrResult}
  AND ocr_hash=#{ocr_hash}

mapper.update:
UPDATE OCR
usr_id=#{usr_id}, ocr_fileName=#{ocr_fileName}, ocr_filePath=#{ocr_filePath}, ocr_datetime=#{ocr_datetime}, ocr_ocrResult=#{ocr_ocrResult}, ocr_hash=#{ocr_hash},
WHERE no=#{no}
