mapper.select:
SELECT *
FROM OCR

  AND ocr_no=#{ocr_no}
  AND usr_id=#{usr_id}
  AND ocr_fileName=#{ocr_fileName}
  AND ocr_filePath=#{ocr_filePath}
  AND ocr_datetime=#{ocr_datetime}
  AND ocr_ocrResult=#{ocr_ocrResult}
  AND ocr_hash=#{ocr_hash}

    SELECT *
    FROM OCR

      AND ocr_no=#{ocr.ocr_no}
      AND usr_id=#{ocr.usr_id}
      AND ocr_fileName=#{ocr.ocr_fileName}
      AND ocr_filePath=#{ocr.ocr_filePath}
      AND ocr_datetime=#{ocr.ocr_datetime}
      AND ocr_ocrResult=#{ocr.ocr_ocrResult}
      AND ocr_hash=#{ocr.ocr_hash}

	LIMIT #{offset}, #{limit}

mapper.update:
UPDATE OCR
usr_id=#{usr_id}, ocr_fileName=#{ocr_fileName}, ocr_filePath=#{ocr_filePath}, ocr_datetime=#{ocr_datetime}, ocr_ocrResult=#{ocr_ocrResult}, ocr_hash=#{ocr_hash},
WHERE no=#{no}
